mapper.insert:

insert into biz_article (author, reviewer, title, 
  content, publish_time, `status`, create_by, create_time, update_by, update_time, is_delete
  )
values (#{author,jdbcType=CHAR}, #{reviewer,jdbcType=CHAR}, #{title,jdbcType=CHAR}, 
  #{content,jdbcType=LONGVARCHAR}, #{publishTime,jdbcType=TIMESTAMP}, #{status,jdbcType=TINYINT}, #{createBy,jdbcType=BIGINT},
#{createTime,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=INTEGER}, #{updateTime,jdbcType=TIMESTAMP},
#{delete,jdbcType=BOOLEAN}
  )

mapper.select:
select

from biz_article
where 1 = 1

  and author = #{author,jdbcType=CHAR}


  and reviewer = #{reviewer,jdbcType=CHAR}


  and title = #{title,jdbcType=CHAR}


  and content = #{content,jdbcType=LONGVARCHAR}


  and publish_time = #{publishTime,jdbcType=TIMESTAMP}


  and `status` = #{status,jdbcType=TINYINT}
